// client/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import LoginPage from './pages/LoginPage';
import AdminDashboard from './pages/dashboards/AdminDashboard';
import FacultyDashboard from './pages/dashboards/FacultyDashboard';
import StudentDashboard from './pages/dashboards/StudentDashboard';
import ErrorPage from './pages/ErrorPage'; 
import Navbar from './components/common/Navbar'; // Import Navbar for the temporary layout

// --- MODIFIED Protected Route Component (Simplified for Preview) ---
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { loading } = useAuth();

  if (loading) {
    return <div className="text-center p-8 text-lg">Loading Application...</div>; 
  }
  
  // ⛔ TEMPORARY BYPASS: We allow access to render the content ⛔
  // We wrap the content in a basic layout since there is no logged-in user to check.
  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navbar will render, but user details will be null, which is fine for structure preview */}
      <Navbar /> 
      <main className="p-8 max-w-7xl mx-auto">
        {children}
      </main>
    </div>
  );
};

// --- Helper for Initial Redirect (Direct to Admin for immediate preview) ---
const HomeRedirect = () => {
    const { loading } = useAuth();
    if (loading) return <div className="text-center p-8 text-lg">Initializing...</div>;
    
    // TEMPORARY: Redirect to the Admin page for immediate viewing when accessing root
    return <Navigate to="/admin" replace />; 
}


const App = () => {
  return (
    <Routes>
      {/* 1. Public Route */}
      <Route path="/login" element={<LoginPage />} />

      {/* 2. Dashboard Routes (Now accessible via direct URL) */}
      <Route 
        path="/admin" 
        element={
          <ProtectedRoute allowedRoles={['Admin']}>
            <AdminDashboard />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/faculty" 
        element={
          <ProtectedRoute allowedRoles={['Faculty']}>
            <FacultyDashboard />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/student" 
        element={
          <ProtectedRoute allowedRoles={['Student']}>
            <StudentDashboard />
          </ProtectedRoute>
        } 
      />

      {/* 3. Error Page and Default Route */}
      <Route path="/error/:code" element={<ErrorPage />} /> 
      <Route path="/" element={<HomeRedirect />} />
      <Route path="*" element={<div className="text-center p-10 text-3xl font-bold text-red-600">404 Page Not Found</div>} />
    </Routes>
  );
};

export default App;
