// client/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import LoginPage from './pages/LoginPage';
import AdminDashboard from './pages/dashboards/AdminDashboard';
import FacultyDashboard from './pages/dashboards/FacultyDashboard';
import StudentDashboard from './pages/dashboards/StudentDashboard';
import ErrorPage from './pages/ErrorPage'; 

// --- MODIFIED Protected Route Component (Bypassing Auth for Preview) ---
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="text-center p-8 text-lg">Loading Application...</div>; 
  }
  
  // ⛔ TEMPORARY BYPASS: We skip the authentication and role checks ⛔
  
  // NOTE: You will still get errors on the dashboard pages if they try
  // to access 'user.role' or 'user.id' when 'user' is null.
  // We handle this by conditionally rendering the Navbar.
  
  return children;
};

// --- Helper for Initial Redirect (Bypassing Auth for Preview) ---
const HomeRedirect = () => {
    const { loading } = useAuth();
    if (loading) return <div className="text-center p-8 text-lg">Initializing...</div>;
    
    // TEMPORARY: Always redirect to Login or a default page for preview
    return <Navigate to="/login" replace />;
}


const App = () => {
  return (
    <Routes>
      {/* 1. Public Route */}
      <Route path="/login" element={<LoginPage />} />

      {/* 2. Error Pages */}
      <Route path="/error/:code" element={<ErrorPage />} /> 
      <Route path="*" element={<div className="text-center p-10 text-3xl font-bold text-red-600">404 Page Not Found</div>} />

      {/* 3. Dashboard Routes (Now accessible via direct URL) */}
      <Route 
        path="/admin" 
        element={
          <ProtectedRoute allowedRoles={['Admin']}>
            <AdminDashboard />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/faculty" 
        element={
          <ProtectedRoute allowedRoles={['Faculty']}>
            <FacultyDashboard />
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/student" 
        element={
          <ProtectedRoute allowedRoles={['Student']}>
            <StudentDashboard />
          </ProtectedRoute>
        } 
      />

      {/* 4. Default route */}
      <Route path="/" element={<HomeRedirect />} />
      
    </Routes>
  );
};

export default App;
