// client/src/utils/api.js
import axios from 'axios';

// Replace with your actual server URL
const API_URL = 'http://localhost:5000/api'; 

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export default api;
