import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../utils/api';

const StudentDashboard = () => {
  const { user, logout } = useAuth();
  const [courses, setCourses] = useState([]);
  const [labSessions, setLabSessions] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [showSubmitModal, setShowSubmitModal] = useState(false);
  const [selectedLab, setSelectedLab] = useState(null);
  const [submissionText, setSubmissionText] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCourses();
    fetchLabSessions();
    fetchSubmissions();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await api.get('/student/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const fetchLabSessions = async () => {
    try {
      const response = await api.get('/student/labsessions');
      setLabSessions(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching lab sessions:', error);
      setLoading(false);
    }
  };

  const fetchSubmissions = async () => {
    try {
      const response = await api.get('/student/submissions');
      setSubmissions(response.data);
    } catch (error) {
      console.error('Error fetching submissions:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post(`/student/submit/${selectedLab._id}`, {
        submissionText,
      });
      setSubmissionText('');
      setShowSubmitModal(false);
      setSelectedLab(null);
      fetchLabSessions();
      fetchSubmissions();
    } catch (error) {
      alert(error.response?.data?.message || 'Error submitting lab work');
    }
  };

  const openSubmitModal = (lab) => {
    // Check if deadline has passed
    const now = new Date();
    const endDate = new Date(lab.endDate);
    if (now > endDate) {
      alert('Submission deadline has passed');
      return;
    }

    // Check if already submitted
    const existingSubmission = submissions.find(s => s.labSessionId._id === lab._id);
    if (existingSubmission) {
      setSubmissionText(existingSubmission.submissionText);
    }

    setSelectedLab(lab);
    setShowSubmitModal(true);
  };

  const getSubmissionForLab = (labId) => {
    return submissions.find(s => s.labSessionId._id === labId);
  };

  const isDeadlinePassed = (endDate) => {
    return new Date() > new Date(endDate);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Student Dashboard</h1>
            <p className="text-sm text-gray-600">Welcome, {user?.email}</p>
          </div>
          <button
            onClick={logout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Courses Section */}
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-4">My Courses</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {courses.length === 0 ? (
              <p className="text-gray-500">No courses enrolled</p>
            ) : (
              courses.map(course => (
                <div key={course._id} className="bg-white shadow rounded-lg p-4">
                  <h3 className="font-semibold text-lg">{course.code}</h3>
                  <p className="text-gray-600">{course.name}</p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Lab Sessions Section */}
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-4">Lab Sessions</h2>
          {loading ? (
            <p>Loading...</p>
          ) : (
            <div className="bg-white shadow rounded-lg overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Course</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Lab Name</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Deadline</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Grade</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {labSessions.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="px-6 py-4 text-center text-gray-500">
                        No lab sessions available
                      </td>
                    </tr>
                  ) : (
                    labSessions.map(lab => {
                      const submission = getSubmissionForLab(lab._id);
                      const deadlinePassed = isDeadlinePassed(lab.endDate);
                      
                      return (
                        <tr key={lab._id}>
                          <td className="px-6 py-4 whitespace-nowrap">
                            {lab.courseId?.code} - {lab.courseId?.name}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap font-medium">{lab.name}</td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <span className={deadlinePassed ? 'text-red-600' : ''}>
                              {new Date(lab.endDate).toLocaleString()}
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            {submission ? (
                              <span className={`px-2 py-1 rounded text-xs ${
                                submission.status === 'Reviewed' ? 'bg-green-100 text-green-800' :
                                submission.status === 'Needs Revision' ? 'bg-yellow-100 text-yellow-800' :
                                'bg-gray-100 text-gray-800'
                              }`}>
                                {submission.status}
                              </span>
                            ) : (
                              <span className="px-2 py-1 rounded text-xs bg-red-100 text-red-800">
                                Not Submitted
                              </span>
                            )}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            {submission?.review?.grade || '-'}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            {!deadlinePassed ? (
                              <button
                                onClick={() => openSubmitModal(lab)}
                                className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
                              >
                                {submission ? 'Update' : 'Submit'}
                              </button>
                            ) : (
                              <span className="text-sm text-gray-400">Deadline Passed</span>
                            )}
                          </td>
                        </tr>
                      );
                    })
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Submissions Section */}
        <div>
          <h2 className="text-xl font-bold mb-4">My Submissions</h2>
          <div className="bg-white shadow rounded-lg overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Lab</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Course</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Submitted At</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Grade</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Feedback</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {submissions.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-gray-500">
                      No submissions yet
                    </td>
                  </tr>
                ) : (
                  submissions.map(submission => (
                    <tr key={submission._id}>
                      <td className="px-6 py-4 whitespace-nowrap font-medium">
                        {submission.labSessionId?.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {submission.labSessionId?.courseId?.code} - {submission.labSessionId?.courseId?.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {new Date(submission.submittedAt).toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 rounded text-xs ${
                          submission.status === 'Reviewed' ? 'bg-green-100 text-green-800' :
                          submission.status === 'Needs Revision' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {submission.status}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {submission.review?.grade || '-'}
                      </td>
                      <td className="px-6 py-4">
                        {submission.review?.feedback ? (
                          <div className="max-w-xs truncate" title={submission.review.feedback}>
                            {submission.review.feedback}
                          </div>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Submit Modal */}
        {showSubmitModal && selectedLab && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-3xl max-h-[90vh] overflow-y-auto">
              <h2 className="text-xl font-bold mb-4">Submit Lab Work</h2>
              <div className="mb-4">
                <p className="font-semibold">{selectedLab.name}</p>
                <p className="text-sm text-gray-600">
                  {selectedLab.courseId?.code} - {selectedLab.courseId?.name}
                </p>
                <p className="text-sm text-gray-600 mt-2">
                  Deadline: {new Date(selectedLab.endDate).toLocaleString()}
                </p>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Submission Text</label>
                  <textarea
                    value={submissionText}
                    onChange={(e) => setSubmissionText(e.target.value)}
                    className="w-full p-2 border rounded"
                    rows="15"
                    placeholder="Enter your lab work here..."
                    required
                  />
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                  >
                    {getSubmissionForLab(selectedLab._id) ? 'Update Submission' : 'Submit'}
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowSubmitModal(false);
                      setSelectedLab(null);
                      setSubmissionText('');
                    }}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default StudentDashboard;
