import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../utils/api';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const [courses, setCourses] = useState([]);
  const [users, setUsers] = useState([]);
  const [showCourseForm, setShowCourseForm] = useState(false);
  const [showEnrollForm, setShowEnrollForm] = useState(false);
  const [loading, setLoading] = useState(true);

  const [courseForm, setCourseForm] = useState({
    name: '',
    code: '',
  });

  const [enrollForm, setEnrollForm] = useState({
    courseId: '',
    facultyId: '',
  });

  useEffect(() => {
    fetchCourses();
    fetchUsers();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await api.get('/admin/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await api.get('/admin/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleCreateCourse = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/courses', courseForm);
      setCourseForm({ name: '', code: '' });
      setShowCourseForm(false);
      fetchCourses();
    } catch (error) {
      alert(error.response?.data?.message || 'Error creating course');
    }
  };

  const handleEnrollFaculty = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/enrollfaculty', enrollForm);
      setEnrollForm({ courseId: '', facultyId: '' });
      setShowEnrollForm(false);
      fetchCourses();
      fetchUsers();
    } catch (error) {
      alert(error.response?.data?.message || 'Error enrolling faculty');
    }
  };

  const facultyUsers = users.filter(u => u.role === 'Faculty');
  const studentUsers = users.filter(u => u.role === 'Student');

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Admin Dashboard</h1>
            <p className="text-sm text-gray-600">Welcome, {user?.email}</p>
          </div>
          <button
            onClick={logout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Action Buttons */}
        <div className="mb-6 flex gap-4">
          <button
            onClick={() => setShowCourseForm(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Create Course
          </button>
          <button
            onClick={() => setShowEnrollForm(true)}
            className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Enroll Faculty
          </button>
        </div>

        {/* Create Course Modal */}
        {showCourseForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-md">
              <h2 className="text-xl font-bold mb-4">Create New Course</h2>
              <form onSubmit={handleCreateCourse}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Course Name</label>
                  <input
                    type="text"
                    value={courseForm.name}
                    onChange={(e) => setCourseForm({ ...courseForm, name: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Course Code</label>
                  <input
                    type="text"
                    value={courseForm.code}
                    onChange={(e) => setCourseForm({ ...courseForm, code: e.target.value.toUpperCase() })}
                    className="w-full p-2 border rounded"
                    required
                  />
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                  >
                    Create
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowCourseForm(false)}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Enroll Faculty Modal */}
        {showEnrollForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-md">
              <h2 className="text-xl font-bold mb-4">Enroll Faculty in Course</h2>
              <form onSubmit={handleEnrollFaculty}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Course</label>
                  <select
                    value={enrollForm.courseId}
                    onChange={(e) => setEnrollForm({ ...enrollForm, courseId: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  >
                    <option value="">Select Course</option>
                    {courses.map(course => (
                      <option key={course._id} value={course._id}>
                        {course.code} - {course.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Faculty</label>
                  <select
                    value={enrollForm.facultyId}
                    onChange={(e) => setEnrollForm({ ...enrollForm, facultyId: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  >
                    <option value="">Select Faculty</option>
                    {facultyUsers.map(faculty => (
                      <option key={faculty._id} value={faculty._id}>
                        {faculty.firstName} {faculty.lastName} ({faculty.email})
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                  >
                    Enroll
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowEnrollForm(false)}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Courses Section */}
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-4">Courses</h2>
          {loading ? (
            <p>Loading...</p>
          ) : (
            <div className="bg-white shadow rounded-lg overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Code</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Faculty Assigned</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {courses.map(course => (
                    <tr key={course._id}>
                      <td className="px-6 py-4 whitespace-nowrap font-medium">{course.code}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{course.name}</td>
                      <td className="px-6 py-4">
                        {course.facultyAssigned?.length > 0 ? (
                          <ul className="list-disc list-inside">
                            {course.facultyAssigned.map(f => (
                              <li key={f._id}>{f.firstName} {f.lastName}</li>
                            ))}
                          </ul>
                        ) : (
                          <span className="text-gray-400">No faculty assigned</span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Users Section */}
        <div>
          <h2 className="text-xl font-bold mb-4">Users</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="bg-white shadow rounded-lg p-4">
              <h3 className="font-semibold mb-2">Faculty ({facultyUsers.length})</h3>
              <div className="space-y-2">
                {facultyUsers.map(faculty => (
                  <div key={faculty._id} className="text-sm">
                    {faculty.firstName} {faculty.lastName} - {faculty.email}
                  </div>
                ))}
              </div>
            </div>
            <div className="bg-white shadow rounded-lg p-4">
              <h3 className="font-semibold mb-2">Students ({studentUsers.length})</h3>
              <div className="space-y-2">
                {studentUsers.map(student => (
                  <div key={student._id} className="text-sm">
                    {student.firstName} {student.lastName} - {student.email}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
