// client/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import LoginPage from './pages/LoginPage';
import AdminDashboard from './pages/dashboards/AdminDashboard';
import FacultyDashboard from './pages/dashboards/FacultyDashboard';
import StudentDashboard from './pages/dashboards/StudentDashboard';

// Component to enforce role-based access
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { user, isAuth, loading } = useAuth();

  if (loading) {
    return <div className="text-center p-8">Loading...</div>; // Simple loading state
  }
  
  if (!isAuth) {
    // Not authenticated, redirect to login
    return <Navigate to="/login" replace />;
  }
  
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    // Authenticated but wrong role, redirect to their main dashboard or a 403 page
    return <Navigate to={`/${user.role.toLowerCase()}`} replace />;
  }

  return children;
};


const App = () => {
  return (
    <Router>
      <Routes>
        {/* Public Route */}
        <Route path="/login" element={<LoginPage />} />

        {/* 4B. Role-Based Protected Routes */}
        <Route 
          path="/admin" 
          element={
            <ProtectedRoute allowedRoles={['Admin']}>
              <AdminDashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/faculty" 
          element={
            <ProtectedRoute allowedRoles={['Faculty']}>
              <FacultyDashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/student" 
          element={
            <ProtectedRoute allowedRoles={['Student']}>
              <StudentDashboard />
            </ProtectedRoute>
          } 
        />

        {/* Default route redirects authenticated users based on role */}
        <Route path="/" element={<HomeRedirect />} />
        
        {/* Fallback route */}
        <Route path="*" element={<div className="text-center p-10 text-xl">404 Not Found</div>} />
      </Routes>
    </Router>
  );
};

// Helper component to handle initial redirection
const HomeRedirect = () => {
    const { user, isAuth, loading } = useAuth();
    if (loading) return <div className="text-center p-8">Loading...</div>;
    if (!isAuth) return <Navigate to="/login" replace />;
    return <Navigate to={`/${user.role.toLowerCase()}`} replace />;
}

export default App;
