// client/src/App.js
import React from 'react';
// REMOVE the BrowserRouter import, if present.
// import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Routes, Route, Navigate } from 'react-router-dom'; 
import { useAuth } from './contexts/AuthContext';
// ... (imports for pages) ...

// ... (ProtectedRoute component remains unchanged) ...
// ... (HomeRedirect component remains unchanged) ...


const App = () => {
  return (
    // REMOVE <Router> tag here! 
    // The Router is now in index.js
    
      <Routes>
        {/* Public Route */}
        <Route path="/login" element={<LoginPage />} />

        {/* Role-Based Protected Routes */}
        {/* ... (all other routes remain the same) ... */}
        
        {/* Default route */}
        <Route path="/" element={<HomeRedirect />} />
        
        {/* Fallback */}
        <Route path="*" element={<div className="text-center p-10 text-3xl font-bold text-red-600">404 Page Not Found</div>} />
      </Routes>
    
    // REMOVE closing </Router> tag!
  );
};

export default App;
