import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../utils/api';

const FacultyDashboard = () => {
  const { user, logout } = useAuth();
  const [courses, setCourses] = useState([]);
  const [labSessions, setLabSessions] = useState([]);
  const [selectedLab, setSelectedLab] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [showLabForm, setShowLabForm] = useState(false);
  const [showReviewModal, setShowReviewModal] = useState(false);
  const [selectedSubmission, setSelectedSubmission] = useState(null);
  const [loading, setLoading] = useState(true);

  const [labForm, setLabForm] = useState({
    courseId: '',
    name: '',
    startDate: '',
    endDate: '',
  });

  const [reviewForm, setReviewForm] = useState({
    grade: '',
    feedback: '',
  });

  useEffect(() => {
    fetchCourses();
    fetchLabSessions();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await api.get('/faculty/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const fetchLabSessions = async () => {
    try {
      const response = await api.get('/faculty/labsessions');
      setLabSessions(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching lab sessions:', error);
      setLoading(false);
    }
  };

  const handleCreateLab = async (e) => {
    e.preventDefault();
    try {
      await api.post('/faculty/labsessions', labForm);
      setLabForm({ courseId: '', name: '', startDate: '', endDate: '' });
      setShowLabForm(false);
      fetchLabSessions();
    } catch (error) {
      alert(error.response?.data?.message || 'Error creating lab session');
    }
  };

  const handleViewSubmissions = async (labId) => {
    try {
      const response = await api.get(`/faculty/submissions/${labId}`);
      setSubmissions(response.data);
      setSelectedLab(labId);
    } catch (error) {
      alert(error.response?.data?.message || 'Error fetching submissions');
    }
  };

  const handleReview = async (e) => {
    e.preventDefault();
    try {
      await api.patch(`/faculty/review/${selectedSubmission._id}`, reviewForm);
      setReviewForm({ grade: '', feedback: '' });
      setShowReviewModal(false);
      setSelectedSubmission(null);
      if (selectedLab) {
        handleViewSubmissions(selectedLab);
      }
    } catch (error) {
      alert(error.response?.data?.message || 'Error reviewing submission');
    }
  };

  const openReviewModal = (submission) => {
    setSelectedSubmission(submission);
    setReviewForm({
      grade: submission.review?.grade || '',
      feedback: submission.review?.feedback || '',
    });
    setShowReviewModal(true);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Faculty Dashboard</h1>
            <p className="text-sm text-gray-600">Welcome, {user?.email}</p>
          </div>
          <button
            onClick={logout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Action Button */}
        <div className="mb-6">
          <button
            onClick={() => setShowLabForm(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Create Lab Session
          </button>
        </div>

        {/* Create Lab Session Modal */}
        {showLabForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-md">
              <h2 className="text-xl font-bold mb-4">Create Lab Session</h2>
              <form onSubmit={handleCreateLab}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Course</label>
                  <select
                    value={labForm.courseId}
                    onChange={(e) => setLabForm({ ...labForm, courseId: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  >
                    <option value="">Select Course</option>
                    {courses.map(course => (
                      <option key={course._id} value={course._id}>
                        {course.code} - {course.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Lab Name</label>
                  <input
                    type="text"
                    value={labForm.name}
                    onChange={(e) => setLabForm({ ...labForm, name: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Start Date</label>
                  <input
                    type="datetime-local"
                    value={labForm.startDate}
                    onChange={(e) => setLabForm({ ...labForm, startDate: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">End Date (Deadline)</label>
                  <input
                    type="datetime-local"
                    value={labForm.endDate}
                    onChange={(e) => setLabForm({ ...labForm, endDate: e.target.value })}
                    className="w-full p-2 border rounded"
                    required
                  />
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                  >
                    Create
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowLabForm(false)}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Review Modal */}
        {showReviewModal && selectedSubmission && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-2xl max-h-[90vh] overflow-y-auto">
              <h2 className="text-xl font-bold mb-4">Review Submission</h2>
              <div className="mb-4">
                <p className="font-semibold">Student: {selectedSubmission.studentId?.firstName} {selectedSubmission.studentId?.lastName}</p>
                <p className="text-sm text-gray-600">{selectedSubmission.studentId?.email}</p>
              </div>
              <div className="mb-4">
                <p className="font-semibold mb-2">Submission:</p>
                <div className="p-3 bg-gray-50 rounded border max-h-60 overflow-y-auto">
                  <pre className="whitespace-pre-wrap text-sm">{selectedSubmission.submissionText}</pre>
                </div>
              </div>
              <form onSubmit={handleReview}>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Grade</label>
                  <input
                    type="text"
                    value={reviewForm.grade}
                    onChange={(e) => setReviewForm({ ...reviewForm, grade: e.target.value })}
                    className="w-full p-2 border rounded"
                    placeholder="e.g., A, B, 85/100"
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Feedback</label>
                  <textarea
                    value={reviewForm.feedback}
                    onChange={(e) => setReviewForm({ ...reviewForm, feedback: e.target.value })}
                    className="w-full p-2 border rounded"
                    rows="4"
                    placeholder="Enter your feedback..."
                  />
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                  >
                    Submit Review
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowReviewModal(false);
                      setSelectedSubmission(null);
                    }}
                    className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Lab Sessions Section */}
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-4">Lab Sessions</h2>
          {loading ? (
            <p>Loading...</p>
          ) : (
            <div className="bg-white shadow rounded-lg overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Course</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Lab Name</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Start Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">End Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {labSessions.map(lab => (
                    <tr key={lab._id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {lab.courseId?.code} - {lab.courseId?.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap font-medium">{lab.name}</td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {new Date(lab.startDate).toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {new Date(lab.endDate).toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <button
                          onClick={() => handleViewSubmissions(lab._id)}
                          className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
                        >
                          View Submissions
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Submissions Section */}
        {selectedLab && (
          <div>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Submissions</h2>
              <button
                onClick={() => {
                  setSelectedLab(null);
                  setSubmissions([]);
                }}
                className="px-3 py-1 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
              >
                Close
              </button>
            </div>
            <div className="bg-white shadow rounded-lg overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Student</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Submitted At</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Grade</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {submissions.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                        No submissions yet
                      </td>
                    </tr>
                  ) : (
                    submissions.map(submission => (
                      <tr key={submission._id}>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {submission.studentId?.firstName} {submission.studentId?.lastName}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {new Date(submission.submittedAt).toLocaleString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 rounded text-xs ${
                            submission.status === 'Reviewed' ? 'bg-green-100 text-green-800' :
                            submission.status === 'Needs Revision' ? 'bg-yellow-100 text-yellow-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {submission.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {submission.review?.grade || '-'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <button
                            onClick={() => openReviewModal(submission)}
                            className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
                          >
                            Review
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default FacultyDashboard;
