// client/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';

import LoginPage from './pages/LoginPage';
import AdminDashboard from './pages/dashboards/AdminDashboard';
import FacultyDashboard from './pages/dashboards/FacultyDashboard';
import StudentDashboard from './pages/dashboards/StudentDashboard';

// --- Protected Route Component ---
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { user, isAuth, loading } = useAuth();

  if (loading) {
    return <div className="text-center p-8 text-lg">Loading Application...</div>; 
  }
  
  if (!isAuth) {
    // 1. Not authenticated: Redirect to login
    return <Navigate to="/login" replace />;
  }
  
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    // 2. Authenticated but wrong role: Redirect to their correct dashboard
    console.warn(`Access Denied: User role ${user.role} tried to access a restricted path.`);
    return <Navigate to={`/${user.role.toLowerCase()}`} replace />;
  }

  // 3. Authorized: Render the children
  return children;
};

// --- Helper for Initial Redirect ---
const HomeRedirect = () => {
    const { user, isAuth, loading } = useAuth();
    if (loading) return <div className="text-center p-8 text-lg">Initializing...</div>;
    if (!isAuth) return <Navigate to="/login" replace />;
    // Redirect to the appropriate dashboard
    return <Navigate to={`/${user.role.toLowerCase()}`} replace />;
}


const App = () => {
  return (
    <Router>
      <Routes>
        {/* Public Route */}
        <Route path="/login" element={<LoginPage />} />

        {/* Role-Based Protected Routes */}
        <Route 
          path="/admin" 
          element={
            <ProtectedRoute allowedRoles={['Admin']}>
              <AdminDashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/faculty" 
          element={
            <ProtectedRoute allowedRoles={['Faculty']}>
              <FacultyDashboard />
            </ProtectedRoute>
          } 
        />
        <Route 
          path="/student" 
          element={
            <ProtectedRoute allowedRoles={['Student']}>
              <StudentDashboard />
            </ProtectedRoute>
          } 
        />

        {/* Default route */}
        <Route path="/" element={<HomeRedirect />} />
        
        {/* Fallback */}
        <Route path="*" element={<div className="text-center p-10 text-3xl font-bold text-red-600">404 Page Not Found</div>} />
      </Routes>
    </Router>
  );
};

export default App;
